    printNewLine();

    for (int i = sInput.size() - 1; i >= 0; --i) {
        char c = sInput[i];
        auto iter = mStorage.find(c);
        if (iter != mStorage.end()) {
            int currValue = iter->second;
            if (currValue < prevValue) {
                nYear -= currValue;
            }else {
                nYear += currValue;
                prevValue = currValue;
            }
        } else {
            std::cout << "---Results---";
            std::cout << "Invalid Roman numeral: " << c << std::endl;
            return 0;
        }
    }
    std::cout << "---Results---" << std::endl;
