            case 'X':
                if (i < s.length() - 1 && (s.charAt(i + 1) == 'L' || s.charAt(i + 1) == 'C')) {
                    value -= 10;
                } else {
                    value += 10;
                }
                break;
            case 'L':
                value += 50;
                break;
            case 'C':
                if (i < s.length() - 1 && (s.charAt(i + 1) == 'D' || s.charAt(i + 1) == 'M')) {
                    value -= 100;
                } else {
                    value += 100;
                }
                break;
            case 'D':
                value += 500;
                break;
            case 'M':
                value += 1000;
        }
    }
