        if (s == null || s.length() == 0) {
            return 0;
        }
        int sum = 0;
        char ch = 0;
        char pre = 0;
        for(int i = 0, len = s.length(); i < len; i ++) {
            pre = ch;
            ch = s.charAt(i);
            switch (ch) {
                case 'I':
                    sum ++;
                    break;
                case 'V':
                    if (pre == 'I') {
                        sum += 3;
                    }
                    else {
                        sum += 5;
                    }
                    break;
                case 'X':
                    if (pre == 'I') {
                        sum += 8;
                    }
                    else {
                        sum += 10;
                    }
                    break;
                case 'L':
                    if (pre == 'I') {
                        sum += 48;
                    }
                    else if (pre == 'V') {
                        sum += 40;
                    }
                    else if (pre == 'X') {
                        sum += 30;
                    }
                    else {
                        sum += 50;
                    }
                    break;
                case 'C':
                    if (pre == 'I') {
                        sum += 98;
                    }
                    else if (pre == 'V') {
                        sum += 90;
                    }
                    else if (pre == 'X') {
                        sum += 80;
                    }
                    else {
                        sum += 100;
                    }
                    break;
                case 'D':
                    if (pre == 'I') {
                        sum += 498;
                    }
                    else if (pre == 'V') {
                        sum += 490;
                    }
                    else if (pre == 'X') {
                        sum += 480;
                    }
                    else if (pre == 'C') {
                        sum += 300;
                    }
                    else {
                        sum += 500;
                    }
                    break;
