class Solution {
public:
    int romanToInt(string s) {
        if(s.empty()) return 0;  // if string 's' is empty this will return 0
        unordered_map<char,int>m = {    //making unordered_map with <key, value> pair  where key is 'char' and value is 'int'.
           {'I', 1},
          {'V',5},
         {'X',10},
         {'L', 50},
        {'C',100},
        {'D',500},
       {'M',1000}
        };
        int sum = 0;          // making variable to store sum,
        for(int i=0; i< s.length()-1; ++i){     // i is a pointer which will go from 0 to s.length()-1  on our string s.
            if(m[s[i]]< m[s[i+1]]){          // if value of pointer  at i is less then value at i+1 (eg: if we consider string 'IVXIX' and consider i is a 'V' 
                                                               which is before 'X' then  sum = 0-5 which is 5.  
                sum-=m[s[i]];
            }else{
                sum+=m[s[i]];  // if value of pointer  at i is more then value at i+1 (eg: if we consider string 'IVXIX' and consider i is a 'X' 
                                                               which is before 'I' then  sum = 0+10 which is 10.  
            }
        }
        sum+=m[s.back()];    // this will take the value from above and push it into end of string and repeat the process till we find our sum.
        return sum;

    }

};

