        char[] ch = s.toCharArray();
        int result = 0;
        int i = 0;
        while(i<ch.length){
                if(ch[i]=='I'){
                    if(i<ch.length-1){
                        if(ch[i+1]=='V'){
                            result = result + 4;
                            i+=2;
                            continue;
                        }else if(ch[i+1]=='X'){
                            result+=9;
                            i+=2;
                            continue;
                        }
                    }
                    result+=1;
                    i+=1;
                    continue;
                }else if(ch[i]=='X'){
                    if(i<ch.length-1){
                        if(ch[i+1]=='L' && i<ch.length-1){
                            result+=40;
                            i+=2;
                            continue;
                        }else if(ch[i+1]=='C' && i<ch.length-1){
                            result+=90;
                            i+=2;
                            continue;
                        }
                    }

                    result+=10;
                    i+=1;
                    continue;
                }else if(ch[i]=='C'){
                    if(i<ch.length-1){
                        if(ch[i+1]=='M'){
                            result+=900;
                            i+=2;
                            continue;
                        }else if(ch[i+1]=='D'){
                            result+=400;
                            i+=2;
                            continue;
                        }
                    }

                    result+=100;
                    i+=1;
                    continue;
                }
            switch (ch[i]){
                case 'V':
                    result+=5;
                    i+=1;
                    continue;
                case 'L':
                    result+=50;
                    i+=1;
                    continue;
                case 'D':
                    result+=500;
                    i+=1;
                    continue;
                case 'M':
                    result+=1000;
                    i+=1;
                    continue;

            }
            i+=1;
        }
        return result;
    }
}

