    for _, path := range pathSum(root.Left, sum - root.Val) {
        res = append(res, append([]int{ root.Val}, path... ))
    }
    for _, path := range pathSum(root.Right, sum - root.Val) {
        res = append(res, append([]int{ root.Val}, path... ))
    }
    return res
}
