        if(!root) return;
        sum-=root->val; //minus the value of current node
        v.push_back(root->val); // push the current to track the path
        //when targetSum becomes zero,then push the path in ans
        if(!root->left && !root->right && sum == 0){
            ans.push_back(v);
            return;
        }
        //left call
        path(root->left,sum,v);
        //right call
        path(root->right,sum,v);
    }

    vector<vector<int>> pathSum(TreeNode* root, int targetSum) {
        if(!root) return {};
        vector<int> v;
        path(root,targetSum,v);
        return ans;
    }
};
