var pathSum = function (root, sum) {
    if (!root) return [];
    const result = [];
    const visited = [];
    function traverse(node) {
        visited.push(node.val);
        if (!node.left && !node.right) {
            const leafSum = [...visited].reduce((a, b) => a+b);
            if (leafSum === sum) result.push([...visited]);
        }
        if (node.left) {
            traverse(node.left);
        }
        if (node.right) {
            traverse(node.right);
        }
        visited.pop();
    }
    traverse(root);
    return result;
};
