class Solution {
public:

    vector<vector<int>> ans;

    void f(TreeNode *root, int target, vector<int> &v){

		//return if root is null
        if(!root) return;

		//if it is a leaf node we insert it's value in our vector 'v' push the answer to vector 'ans' and pop back 'v' again to backtrack
        if(root->left==NULL && root->right==NULL && target-root->val==0){
            v.push_back(root->val);
            ans.push_back(v);
            v.pop_back();
            return;
        }

		//we have two choices 
		//either to take left node path or to take right node path
        //In case we take the left node path we insert it into the vector 'v' in case it may come out to be the answer
		v.push_back(root->val);

		//traverse left in the tree
        f(root->left, target-root->val, v);

		//this is the backtracking part. If the path existed then it would have been taken care of by our custom base case
		//we are passing vector 'v' as a reference thats's why we are taking care of it while we are backtracking. It also reduces the space complexity and time complexity.
        v.pop_back();

		//same again we repeat fot the right part
		//push right part value to vector
        v.push_back(root->val);

		//traverse into the right part
        f(root->right, target-root->val, v);

		//backtrack
        v.pop_back();

    }

    vector<vector<int>> pathSum(TreeNode* root, int targetSum) {

        vector<int> v;
        f(root, targetSum, v);        
        return ans;
    }
};
```

