var pathSum = function(root, sum) {
    const res = [];
	// helper recursive function
    const helper = (node, total, arr) => {
        if(!node) return;
        if(node.val === total && !node.left && !node.right) {
            arr.push(node.val);
            res.push(arr.slice());
            arr.pop();
            return;
        }
		// add current element into the array
        arr.push(node.val);
		// visit left branch
        helper(node.left, total - node.val, arr)
		// visit right branch
        helper(node.right, total - node.val, arr);
		// backtrack 
        arr.pop()
    }
  // invoke helper function and return result array
    helper(root, sum, []);  
    return res;
