class Solution {
public:
	vector<vector<int>> pathSum(TreeNode* root, int sum) {
		if (!root) return vector<vector<int>>{};
		vector<vector<int>> rc;
		queue<tuple<TreeNode*, vector<int>, int> > q;
		q.push({ root, vector<int>{}, sum });
