    private void findAllPath(int index, TreeNode root, int sum) {
        if (root == null) {
            return;
        }
        path[index] = root.val;
        if (root.left == null && root.right == null && root.val == sum) {
            List<Integer> list = new ArrayList<Integer>();
            for (int i = 0; i <= index; i++) {
                list.add(path[i]);
