class Solution {
public:
    vector<vector<int>> result;
    void solve(TreeNode* root, int targetSum, vector<int>temp){
        if(!root)   return;
        if(!root->left && !root->right){
            temp.push_back(root->val);
            targetSum-= root->val;
            if(targetSum == 0)  result.push_back(temp);
            return;
        }
        temp.push_back(root->val);
        if(root->left)  solve(root->left, targetSum-root->val, temp);
        if(root->right) solve(root->right, targetSum-root->val, temp);
    }
    vector<vector<int>> pathSum(TreeNode* root, int targetSum) {
        solve(root, targetSum, {});
        return result;
    }
};
