 public List<List<Integer>> pathSum(TreeNode root, int targetSum) {
        List<List<Integer>> out=new ArrayList();
        if(root==null){
            return out;
        }
        LinkedList<Integer> loc=new LinkedList();
        loc.add(root.val);
