using path = pair<TreeNode*, pair<vector<int>, int>>;

class Solution {
public:
    vector<vector<int>> pathSum(TreeNode *root, int target) {
        // edge case: empty tree
        if (!root) return {};
        // support variables
        vector<vector<int>> res;
        vector<int> tmp;
        queue<path> q;
        int qLen = 1;
        q.push({root, {{root->val}, root->val}});
        // BFS
        while (qLen) {
            while (qLen--) {
                // extracting the front
                path curr = q.front();
                q.pop();
                // checking for matching leafs
                if (!curr.first->left && !curr.first->right && curr.second.second == target)
                    res.push_back(curr.second.first);
                if (curr.first->left) {
                    tmp = curr.first->right ?  vector<int>(begin(curr.second.first), end(curr.second.first)) : curr.second.first;
                    tmp.push_back(curr.first->left->val);
                    q.push({curr.first->left, {tmp, curr.second.second + curr.first->left->val}});
                }
                if (curr.first->right) {
                    curr.second.first.push_back(curr.first->right->val);
                    q.push({curr.first->right, {curr.second.first, curr.second.second + curr.first->right->val}});
                }
            }
            // preparing for the next loop
            qLen = q.size();
        }
        return res;
    }
};
