object Solution {
    def pathSum(root: TreeNode, sum: Int): List[List[Int]] = {
        rec(root, sum, List.empty[List[Int]], List.empty[Int])
    }

    def rec(node: TreeNode, sum: Int, result: List[List[Int]], current: List[Int]): List[List[Int]] = {
        if (node == null) result        
        else if (node.left == null && node.right == null) if (node.value == sum) (node.value :: current).reverse :: result else result
        else {
            val newSum = sum - node.value
            val newCurrent = node.value :: current
            val newResult = rec(node.left, newSum, result, newCurrent)
            rec(node.right, newSum, newResult, newCurrent)
        }     
    }
}
