class Solution {
public:
    vector<vector<int>> paths;
    vector<int> path; bool p=false;

    void pathtrace(TreeNode* root, int sum)
    {
        if(!root)
            return;

        path.push_back(root->val);
        if(!root->left && !root->right && sum==root->val)
            paths.push_back(path);

        pathtrace(root->left, sum-root->val);
        pathtrace(root->right, sum-root->val);
        path.pop_back();
    }

    vector<vector<int>> pathSum(TreeNode* root, int targetSum) 
    {
        if(!root)
            return {};

        pathtrace(root, targetSum);
        return paths;
    }
};
