var pathSum = function(node, targetSum, paths = [], rootToLeaf = []) {
    if(node) {
        const remaining = targetSum - node.val;

        rootToLeaf.push(node.val); 

        if(!node.left && !node.right && remaining === 0) paths.push([...rootToLeaf]);

        pathSum(node.left, remaining, paths, rootToLeaf);
        pathSum(node.right,remaining, paths, rootToLeaf);

        rootToLeaf.pop();
    }

    return paths;
};
