            for(int i=0; i<n; ++i){
             auto curr=q.front();q.pop();
            if(curr.first == d) ans = min(ans, curr.second);   
            for(auto x:  g[curr.first]){             
               if( curr.second + x.second > ans) continue;
                q.push({x.first,curr.second + x.second });
