int findCheapestPrice(int n, vector<vector<int>>& flights, int src, int end, int k) {
        vector<int> dst(n,1e7);
        vector<int> temp(n,1e7);
        dst[src]=0;
        temp[src]=0;
        for(int i=0;i<=k;++i)
        {
            for(vector<int> j:flights)
            {
                if(temp[j[1]] > dst[j[0]]+j[2])
                    temp[j[1]] = dst[j[0]]+j[2];
            }
            for(int j=0;j<n;++j)
                dst[j] = temp[j];
        }
        if(dst[end]==1e7)
            return -1;
        else
            return dst[end];
    }

