class Solution {
public:
    vector<pair<int,int> > adj[101];
    int dest;
    int ans=INT_MAX;
    int tot_stops;
    bool vis[101]={0};
    int dp[101][101];
    int dfs(int u,int stops){
        // for a node its optimal value can come from its child
        if(u==dest){
            return 0;
        }
        if(stops<=-1)return INT_MAX;
        if(dp[u][stops]!=-1)return dp[u][stops];
        int ret_val=INT_MAX;
        for(auto [v,p]: adj[u]){
            int val = dfs(v,stops-1);
			// if impossible to reach the destination from v
            if(val!=INT_MAX)
            ret_val = min(ret_val, p+val);
        }
        return dp[u][stops]=ret_val;  
    }


    int findCheapestPrice(int n, vector<vector<int>>& flights, int src, int dst, int k) {
        for(int i=0;i<n;i++){
            adj[i].clear();
            vis[i]=0;
            for(int j=0;j<=k;j++){
                dp[i][j]=-1;
            }
        }
        for(int i=0;i<flights.size();i++){
            adj[flights[i][0]].push_back({flights[i][1],flights[i][2]});
        }
        dest=dst;
        tot_stops=k+1;
        ans = dfs(src,k);
        return (ans>(1e9))?-1:ans; 
    }
};
