func DFS(root, dist, depth int, graph [][][]int, dst, K int, min *int) {
    if depth > K + 1 { return }
    if root == dst && dist < *min { *min = dist }
    for _, edge := range graph[root] {
        if temp := dist + edge[1]; temp < *min {
            DFS(edge[0], temp, depth+1, graph, dst, K, min)
        }
    }
}
