class Solution {
    public int findCheapestPrice(int n, int[][] flights, int src, int dst, int k) {
        // map of <node (from) -> <node (to), cost of from - to>>
        Map<Integer, List<int[]>> adj = new HashMap<>();
        for (int[] f : flights) {
            adj.putIfAbsent(f[0], new ArrayList<>());
            adj.get(f[0]).add(new int[] {f[1], f[2]});
        }

        // array to record minimum steps to reach a particular node
        int[] stops = new int[n];
        Arrays.fill(stops, Integer.MAX_VALUE);  // initialise with int_max

        // min heap which will always give us least cost journey
        // {node, cost_from_src_node, number_of_stops_from_src_node}
        PriorityQueue<int[]> pq = new PriorityQueue<>((a, b) -> a[1] - b[1]);
        pq.offer(new int[] {src, 0, 0});    // starts with node 0, cost 0 and 0 steps

        while (!pq.isEmpty()) {
            int[] currDetails = pq.poll();
            int currNode = currDetails[0];
            int costSoFar = currDetails[1];
            int stepsSoFar = currDetails[2];

            // till the current node, if we have already encountered a path wi lesser stops
            // or if the number of stops exceeds the limit, we will skip
            if (stepsSoFar > stops[currNode] || stepsSoFar > k + 1) {
                continue;
            }
            // otherwise, it means that we have reached the current node with lesser number of stops
            // so we put the that new number of stops so far at index 'currNode'
            stops[currNode] = stepsSoFar;
            if (currNode == dst) {  // if current node is our destination
                return costSoFar;   // return the cost incurred so far
            }
            // if we don't haveany outoing edge from our current node,
            // means we can't move further, therefore skip
            if (!adj.containsKey(currNode)) {
                continue;
            }
            // else, record all the nodes we can visit from current node
            for (int[] next : adj.get(currNode)) {
                int nextNode = next[0];
                // the cost till next node is current cost so far + 
                // cost of travelling from current node to next node (edge weight)
                int costToNextNode = costSoFar + next[1];
                // push them to the heap (we take one more step so 'steps + 1')
                pq.offer(new int[] {nextNode, costToNextNode, stepsSoFar + 1});
            }
        }

        // if in any way, we didn't reach dst, we will return -1
        return -1;
    }
}
