class Solution {
    public int findCheapestPrice(int n, int[][] flights, int src, int dst, int k) {
        int graph[][] = new int[n][n];

        //creating matrix that represents grpah
        //graph[i][j] = price to travel from i to j
        for (int i = 0; i < flights.length; i++) {
            graph[flights[i][0]][flights[i][1]] = flights[i][2];
        }


        //find cheapest price with k stops(k + 1 flights)
        int ans = findCheapestPrice(src, dst, graph, k + 1, new int[n + 1][n + 1][k + 2]);

        return ans >= 1000000 ? -1 : ans;
    }

    /*
        finds cheapest price with k flights from src to dst 
		O(N * N * K)
    */
    int findCheapestPrice(int src, int dst, int graph[][], int k, int dp[][][]) {

        //reached to destination
        if (dst == src) {
            return 0;
        }

        //no more flights left
        if (k == 0) {
            return 1000000;
        }

        //already calculated then return
        if (dp[src][dst][k] != 0) {
            return dp[src][dst][k];
        }


        //initializing min to some greater number
        //for each possible edge from src in graph traverse and caluculate distance
        //keep  minimum distance 
        int min = 1000000;
        for (int i = 0; i < graph[src].length; i++) {
            if (graph[src][i] != 0) {
                min = Math.min(min, graph[src][i] + findCheapestPrice(i, dst, graph, k - 1, dp));
            }
        }

        return dp[src][dst][k] = min; 
    }
}
