BELLMAN-FORD
```
class Solution {
    public int findCheapestPrice(int n, int[][] flights, int src, int dst, int k) {
	//here dp[i,j] means reaching jth vertex using atmost i hops
        int[][] dp = new int[k+2][n];

        for(int i=0;i<k+2;i++){
            Arrays.fill(dp[i],Integer.MAX_VALUE);
        }]

        for(int i = 0; i <= k+1; i++){
            dp[i][src] = 0; // src is at a dist 0, no matter how many hops u take
        }

        for(int i = 1; i <= k+1; i++){
            for(int[] f: flights){
                int u = f[0];
                int v = f[1];
                int w = f[2];

                if(dp[i-1][u] != Integer.MAX_VALUE)
                    dp[i][v] = Math.min(dp[i][v], dp[i-1][u] + w);
            }
        }

        return (dp[k+1][dst] == Integer.MAX_VALUE)? -1: dp[k+1][dst];
    }
}
