        Map<Integer, List<Integer[]>> adj = new HashMap();
        int[] vis = new int[n];
        Arrays.fill(vis, Integer.MAX_VALUE);
        for(int[] flightcost : flights){
            adj.computeIfAbsent(flightcost[0], z -> new ArrayList()).add(new Integer[]{flightcost[1],flightcost[2]});
        }
        //Priority queue contains Integer array with 3 elements in order -> vertex, cost, stop 
        Queue<Integer[]> que = new PriorityQueue<Integer[]>((o1,o2) -> {return o1[1]-o2[1];});        
        que.add(new Integer[]{src, 0, -1});

        while(!que.isEmpty()){
            Integer[] cur = que.poll();
            Integer curVertex = cur[0], curCost = cur[1], curStops = cur[2];
            if(curVertex == dst) return curCost;

            if(vis[curVertex] <= curStops || curStops == k || !adj.containsKey(curVertex)) continue;
            vis[curVertex] = curStops;
            for(Integer[] next : adj.get(curVertex)){
                que.add(new Integer[]{next[0], curCost + next[1], curStops+1});
            }            
        }
        return -1;
    }

