    int findCheapestPrice(int n, vector<vector<int>>& flights, int src, int dst, int K) {
        vector<vector<pair<int, int> > > graph(n);
        for (auto & f : flights) {
            graph[f[0]].push_back({f[1], f[2]});
        }

        typedef tuple<int, int, int> Type;
        priority_queue<Type, vector<Type>, std::greater<Type>> que;
        vector<pair<int, int> > cost(n, {INT_MAX, INT_MAX}); //dist, k

        que.emplace(0, -1, src);        
        while(que.size()) {
            auto [dist, cur_k, cur] = que.top();
            que.pop();

            if (cur == dst && cur_k <= K) {
                return dist;
            }
            if (cur_k >= K) {
                continue;
            }

            // similar to check visited
            if (dist >= cost[cur].first && cur_k >= cost[cur].second) {
                continue;
            }
            // similar to update visited
            if (dist <= cost[cur].first && cur_k <= cost[cur].second) {
                cost[cur] = {dist, cur_k};
            }

            for (auto [ne, edge] : graph[cur]) {
                int dist0 = dist + edge;
                // similar to check visited
                if (dist0 < cost[ne].first || cur_k + 1 < cost[ne].second) {
                    que.emplace(dist0, cur_k + 1, ne);
                }
            }
        }
        return -1;
    }
