    using VVI = vector<VI>;
    int findCheapestPrice(int N, VVI& E, int S, int T, int K, int INF = 1e9 + 7) {
        VI pre(N, INF); pre[S] = 0;
        while (0 <= K--) {
            VI cur{ pre };
            for (auto& edge: E) {
                auto [u, v, w] = tie(edge[0], edge[1], edge[2]);
                cur[v] = min(cur[v], pre[u] + w);
            }
            swap(pre, cur);
        }
        return pre[T] != INF ? pre[T] : -1;
    }
