class Solution {
    ArrayList<int[]>[] graph;
    public int findCheapestPrice(int n, int[][] flights, int src, int dst, int K) {
        graph = new ArrayList[n];
        for(int i = 0; i < n; i++){
            graph[i] = new ArrayList<>();
        }
        for(int[] flight: flights){
            graph[flight[0]].add(new int[]{flight[1], flight[2]});
