        PriorityQueue<int[]> pq = new PriorityQueue<>(new CustomComp());
        pq.offer(new int[]{0, src, K+1});
        while(!pq.isEmpty()){
            int[] cur = pq.poll();
            if(cur[1] == dst) return cur[0];
            if(cur[2]>0){
                for(int[] nxt: graph[cur[1]]){
                    pq.offer(new int[]{cur[0]+nxt[1], nxt[0], cur[2]-1});
                }
            }
        }
        return -1;
    }
