        for (let i = 0; i < levelSize; i++) {
            let [cur, costSoFar] = queue.shift()

            if (costSoFar > minCost) {
                continue
            }

            if (cur === dst) {
                minCost = Math.min(minCost, costSoFar)
                continue
            }

            let neighbors = graph[cur] || []
            for (let [nei, cost] of neighbors) {
                queue.push([nei, costSoFar + cost])
            }
        }
    }

    return minCost === Infinity ? -1 : minCost
};

