class Solution {
public:
    int findCheapestPrice(int n, vector<vector<int>>& flights, int src, int dst, int k) 
    {
        queue<array<int,3>> pq;

        pq.push({0,src,0});

        vector<int> dist(n,1e9);
        dist[src] = 0;
        vector<vector<int>> adj[n];

        for(auto i : flights)
        {
            int x = i[0], y = i[1], wt = i[2];

            adj[x].push_back({y,wt});
        }

        while(!pq.empty())
        {
            auto node = pq.front();
            pq.pop();

            int u = node[1];
            int steps = node[2];
            int d = node[0];

