class Solution {
public:
    int findCheapestPrice(int n, vector<vector<int>>& flights, int src, int dst, int k) {
        // controlled bfs
        vector<vector<pair<int,int>>> adjList(n);
        for(vector<int> &e : flights) {
            adjList[e[0]].push_back({e[1],e[2]});
        }
        vector<int> cost(n,INT_MAX);
        cost[src] = 0;
        queue<pair<int,int>> q;
        q.push({src,0});
        int level = 0;
        while(level <= k && !q.empty()) {
            int sz = q.size();
            for(int i=0;i<sz;i++) {
                int node = q.front().first;
                int baseCost = q.front().second;
                q.pop();
                for(auto &[des,price] : adjList[node]) {
                    if(baseCost + price <= cost[des]) {
                        q.push({des,baseCost + price});
                        cost[des] = baseCost + price;
                    }
                }
            }
            level ++;
        }
        if(cost[dst] == INT_MAX)
            return -1;
        return cost[dst];
    }
};
```

