        //pruning:
        if(k<0) return 1e9;

        //base case:
        if(src == dstn){
            return 0;
        }

        //cache check:
        if(dp[src][k]!=-1) return dp[src][k];

        //computations:
        int ans = 1e9;
        for(auto child: adjList[src]){

