class Solution {
    public int findCheapestPrice(int n, int[][] flights, int src, int dest, int k) {

        // Adjacency List....

        List<List<Node>> adj = new ArrayList<>();
        for(int i=0; i<n; i++) adj.add(new ArrayList<>());

        for(int i=0; i<flights.length; i++) adj.get(flights[i][0]).add(new Node(flights[i][1], flights[i][2]));

        PriorityQueue<Pair> pq = new PriorityQueue<>();
        pq.offer(new Pair(new Node(src, 0), 0));

        int[] cost = new int[n];
        Arrays.fill(cost, (int)1e8);
        cost[src] = 0;

        while(!pq.isEmpty()){
            Pair front = pq.poll();

            if(front.numStop > k) continue;
            for(Node nbr : adj.get(front.node.src)){
                if(cost[front.node.src] + nbr.price < cost[nbr.src] && front.numStop <= k) {
                    cost[nbr.src] = cost[front.node.src] + nbr.price;
                    pq.offer(new Pair(new Node(nbr.src, cost[nbr.src]), front.numStop+1));

                }
            }

        }


        if(cost[dest] == (int)1e8) return -1;
        return cost[dest];
    }
}

