#include <vector>

class Tri {
  private:
    int n = 38;
    std::vector<int> nums;

  public:
    int tribo(int k) {
      if (k == 0) return 0;
      if (k >= (int)nums.size()) {
        nums.resize(k + 1);
      }
      if (nums[k] != 0) return nums[k];

      nums[k] = tribo(k - 1) + tribo(k - 2) + tribo(k - 3);
      return nums[k];
    }

    Tri() {
      nums.resize(n);
      nums[1] = 1;
      nums[2] = 1;
      tribo(n - 1);
    }
};

