        if(n <= 2) {
            return Math.min(1, n);
        }
        int a = 0, b = 1, c = 1;
        int cur = 0;
        for (int i = 3; i <= n; i++) {
            cur = a + b + c;
            a = b;
            b = c;
            c = cur;
        }
        return cur;
    }
}
