class Solution {

    public int tribonacci(int n) {
        int[] aux = new int[n+1];
        return findTribonacci(aux,n);    
    }
    private int findTribonacci(int[] arr,int n){
        if(n == 0) return 0;
        if(n == 1) return 1;
        if(n == 2) return 1;
        int first = 0;
        if(arr[n-1] > 0) first = arr[n-1];
        else{
             first = findTribonacci(arr,n-1);
             arr[n-1] = first;
        }

        int second = 0;
        if(arr[n-2] > 0) second = arr[n-2];
        else{
             second = findTribonacci(arr,n-2);
             arr[n-2] = second;
        }

        int third = 0;
        if(arr[n-3] > 0) third = arr[n-3];
        else{
             third = findTribonacci(arr, n-3);
             arr[n-3] = third;
        }
        return first + second + third;
    } 
}
