    if (N == 0) return 0;
    if (N <= 2) return 1;
    for (let i = 3; i <= N; ++i)
        d = a + b + c,        // dp[i] = dp[i - 3] + dp[i - 2] + dp[i - 1]
        a = b, b = c, c = d;  // 👈 slide window
    return d;
};
