class Solution {

    private Map<Integer, Integer> map = new HashMap<>();

    public int tribonacci(int n) {
        if (n == 2 || n == 1){
            return 1;
        }
        if (n == 0) {
            return 0;
        }

        return getValue(n - 3) + getValue(n - 2) + getValue(n - 1);     

    }
    private int getValue(int value) {

        int a = map.getOrDefault(value,-1);

        if (a == -1) {
            a = tribonacci(value);
            map.put(value, a);
        }

        return a;
    }
}
