    int numTilings(int n) {
        if(n <= 2) return n;
		// filled_prev = dp[i-1][0], filled_prev2 = dp[i-2][0]...same for gap_prev & gap_prev2
        int filled_prev = 2, gap_prev = 2, filled_prev2 = 1, gap_prev2 = 1;
        for(int i = 3; i <= n; i++) {
            int filled = (filled_prev + filled_prev2 + 2l*gap_prev2) % MOD;
