    int numTilings(int N) {
        int a = 0, b = 1, c = 1, c2, mod = 1e9 + 7;
        while (--N) {
            c2 = (c * 2 % mod + a) % mod;
            a = b;
            b = c;
            c = c2;
        }
        return c;
    }
