		int[] f = new int[N + 1];

        f[1] = 1;
        f[2] = 2;
        f[3] = 5;
        for(int i = 4; i <= N; i++) {
            f[i] = 2 * f[i - 1] % mod + f[i - 3] % mod;
            f[i] %= mod;
        }
        return (int) f[N];
    }
}
