public int numTilings(int n) {
	int mod = 1_000_000_007;
	long dp[][] = new long[n+1][3];
	// entire "zero-th" row is possible because DP \xAF\_(ツ)_/\xAF
	dp[0][0] = 1;
	dp[0][1] = 1;
	dp[0][2] = 1;
	// we can start with a full row 1 by placing single domino (▉)
	// other states are impossible because monominoes don't exist
	dp[1][0] = 0;
	dp[1][1] = 0;
	dp[1][2] = 1;

	for (int i = 2; i<=n; i++) {
		// calculate all the ways to get to ▉▉▀
		dp[i][0] += dp[i-1][1]; // ▉▃ + ▀▀
		dp[i][0] += dp[i-2][2]; // ▉  + ▉▀
		dp[i][0] = Math.floorMod(dp[i][0], mod);

		// calculate all the ways to get to ▉▉▃
		dp[i][1] += dp[i-1][0]; // ▉▀ + ▃▃
		dp[i][1] += dp[i-2][2]; // ▉  + ▉▃
		dp[i][1] = Math.floorMod(dp[i][1], mod);

		// calculate all the ways to get to ▉▉▉
		dp[i][2] += dp[i-1][2]; // ▉▉ + ▉
		dp[i][2] += dp[i-2][2]; // ▉  + ══
		dp[i][2] += dp[i-1][0]; // ▉▀ + ▃▉
		dp[i][2] += dp[i-1][1]; // ▉▃ + ▀▉
		dp[i][2] = Math.floorMod(dp[i][2], mod);
	}

	return (int) dp[n][2];
}
```

