		dp[i][2] += dp[i-2][2]; // ▉  + ══
		dp[i][2] += dp[i-1][0]; // ▉▀ + ▃▉
		dp[i][2] += dp[i-1][1]; // ▉▃ + ▀▉
		dp[i][2] = Math.floorMod(dp[i][2], mod);
	}

	return (int) dp[n][2];
}
