const unsigned int M = 1000000007;
class Solution {
public:
    int n;
    int dp[1000][2][2];
    int numTilings(int n) {
        this->n=n;
        memset(dp,-1,sizeof(dp));
        return f(0,0,0);
        // return 0;

    }
    int f(int i,bool u , bool d){
        if(i==n ){
            if(!u && !d) return 1;
            return 0;
        }
        if(dp[i][u][d]!=-1) return dp[i][u][d];
        int ans=0;

        if(u^d){
            ans+=f(i+1,1,1)%M;//1 traingular 
            ans+=f(i+1,!u,!d)%M;//1 horizontal 
        }

        else if(u==1 && d==1) ans+=f(i+1,0,0)%M;//just empty cel;
        else if(u==0 && d==0){
            ans+=f(i+1,1,1)%M;//2 horizontal
            ans+=f(i+1,0,0)%M;//1 vertical
            ans+=f(i+1,0,1)%M;//1 traiangualar 
            ans+=f(i+1,1,0)%M;//1 down triangular
        }
        return dp[i][u][d]=ans%M;
    }
};
