        if(n<=3){
            return dp[n];
        }
        //If n>3
        for(int i=4;i<=n;i++){
            dp[i]=2*dp[i-1]+dp[i-3];
            dp[i]%=mod;
        }
        return dp[n]%mod;
    }
};
