class Solution {
    static final int mod = 1000000007;
    long[][] base = {{0, 1, 0}, {0, 0, 1}, {1, 0, 2}};
    public long[][] pow(int n){
        if(n == 1){
            long mt[][] = new long[3][3];
            for(int i = 0; i < 3; i++){
                for(int j = 0; j < 3; j++){
                    mt[i][j] = base[i][j];
                }
            }
            return mt;
        }
        long[][] f = pow(n / 2);
        long tmp[][] = new long[3][3];
        for(int i = 0; i < 3; i++){
            for(int j = 0; j < 3; j++){
                tmp[i][j] = 0;
                for(int k = 0; k < 3; k++){
                    tmp[i][j] += ((f[i][k] * f[k][j]) % mod);
                    tmp[i][j] %= mod;
                }
            }
        }
        for(int i = 0; i < 3; i++){
            for(int j = 0; j < 3; j++){
                f[i][j] = tmp[i][j];
            }
        }
        if(n % 2 == 1){
            tmp = new long[3][3];
            for(int i = 0; i < 3; i++){
                for(int j = 0; j < 3; j++){
                    tmp[i][j] = 0;
                    for(int k = 0; k < 3; k++){
                        tmp[i][j] += ((base[i][k] * f[k][j]) % mod);
                        tmp[i][j] %= mod;
                    }
                }
            }
            for(int i = 0; i < 3; i++){
                for(int j = 0; j < 3; j++){
                    f[i][j] = tmp[i][j];
                }
            }
        }
        return f;
    }
    int a = 1, b = 2, c = 5;
    public int numTilings(int N) {
        int n = N;
        if(n == 1) return a;
        if(n == 2) return b;
        if(n == 3) return c;
        long result[][] = pow(N - 3);
        long ans = (result[2][0] * a % mod + result[2][1] * b % mod) % mod + result[2][2] * c % mod;
        int xx = (int)(ans % mod);
        return xx;
    }
}
