class Solution {
public:
    int dp[1005][1005];
    int MOD = 1000000007;
    int solve(int up, int down)
    {
        if(up==0 && down==0)
            return 1;
        if(up<0 || down<0)
            return 0;
        if(dp[up][down]!=-1)
            return dp[up][down];
        int ans;
        //if up==down then all four types are 1=>put vrrtical 2x1 domino, 2=>put 2 horizontal domino on each other, 3=>put one tromino, 4=>put inverted tromino
        if(up==down)
        {
            ans = (((solve(up-1, down-1)%MOD + solve(up-2, down-2)%MOD)%MOD + solve(up-2, down-1)%MOD)%MOD + solve(up-1, down-2)%MOD)%MOD; 
        }
        //if up is more then down, then we have two cases, 1=>put inverted tromino, 2=>put one horizontal domino at top
        else if(up==down+1)
        {
            ans = (solve(up-2, down-1)%MOD +solve(up-2, down)%MOD)%MOD;
        }
        //if up is less then down, then we have two cases, 1=>put tromino, 2=>put one horizontal domino at bottom
        else if(up==down-1)
        {
            ans = (solve(up-1, down-2)%MOD + solve(up, down-2)%MOD)%MOD;
        }
        return dp[up][down] = ans;
    }
    int numTilings(int N) {
        memset(dp, -1, sizeof(dp));
        return solve(N,N);
    }
};
