int n;
int mod=1e9+7;
int dp[1001][2][2];
int fun(int i,bool u,bool l){

    if(i>n) return 0;
    if(i==n){
        if(u==0 && l==0) return 1;
        return 0;
    }
    if(dp[i][u][l]!=-1) return dp[i][u][l];
        int ans=0;
    if(u==0 && l==0){
        //vertical
        ans=(ans+fun(i+1,0,0))%mod;
        //horizontal
        ans=(ans+fun(i+2,0,0))%mod;
        //triangular
        ans=(ans+fun(i+1,1,0))%mod;
        //ulta tragunlar
        ans=(ans+fun(i+1,0,1))%mod;
    }

    if(u==1 && l==1){
        ans=(ans+fun(i+1,0,0))%mod;
    }

    if(u^l){
        // if(u==1)
         ans=(ans+fun(i+1,!u,!l))%mod;

        ans=(ans+fun(i+1,1,1))%mod;
